options.script:
let traces = [];
const colors = {
  1: 'green',
  0: 'red'
};

// 1. Get unique validator addresses
const addresses = [...new Set(data.series.flatMap(series =>
  series.fields.find(f => f.name === 'address').values.toArray()
))];

// 2. Map validator address to numeric position
const addressIndex = {};
addresses.forEach((addr, idx) => { addressIndex[addr] = idx; });

// 3. Control vertical spacing and jitter
const verticalSpacing = 20;  // Controls space between validators
const jitterAmount = 0.3;   // Minor random jitter to avoid perfect overlap

// 4. Prepare points & traces
data.series.forEach((series) => {
  let heightField = series.fields.find(f => f.name === 'height');
  let addressField = series.fields.find(f => f.name === 'address');
  let signedField = series.fields.find(f => f.name === 'signed');

  for (let i = 0; i < series.length; i++) {
    const xValue = heightField.values.get(i);
    const address = addressField.values.get(i);
    const yValue = addressIndex[address] * verticalSpacing + (Math.random() - 0.5) * jitterAmount;
    const signed = signedField.values.get(i);

    traces.push({
      x: [xValue],
      y: [yValue],
      mode: 'markers',
      type: 'scatter',
      marker: {
        color: colors[signed],
        size: 20,
        symbol: 'square',
        line: { width: 2, color: 'black' }  // Optional visual separation
      },
      showlegend: false
    });
  }
});

// 5. Return plot config
return {
  data: traces,
  layout: {
    font: {
      family: 'Inter, Helvetica, Arial, sans-serif'
    },
    margin: { l: 0, r: 0, b: 0, t: 30 },
    xaxis: {
      title: 'Block Height',
      type: 'linear',
      autorange: true,
      automargin: true,
      tickformat: ',d'
    },
    yaxis: {
      title: 'Validator',
      type: 'linear',
      automargin: true,
      tickvals: addresses.map((_, idx) => idx * verticalSpacing),
      ticktext: addresses
    },
    showlegend: false
  }
};
