options.script:
let traces = [];
const colors = {
  1: 'green',
  0: 'red'
};

console.log("DATA SERIES:", data.series);

// 1. Build moniker lookup from series[1] (moniker data)
const monikerSeries = data.series[1];
const addressFieldMoniker = monikerSeries.fields.find(f => f.name === 'address');
const monikerField = monikerSeries.fields.find(f => f.name === 'moniker');
const monikerMap = {};
for (let i = 0; i < monikerSeries.length; i++) {
  const addr = addressFieldMoniker.values.get(i);
  const moniker = monikerField.values.get(i);
  monikerMap[addr] = moniker;
}

// 2. Get unique validator addresses from series[0] (signing data)
const signingSeries = data.series[0];
const addressFieldSigning = signingSeries.fields.find(f => f.name === 'address');
const addresses = [...new Set(addressFieldSigning.values.toArray())];

// 3. Map validator address to numeric position
const addressIndex = {};
addresses.forEach((addr, idx) => { addressIndex[addr] = idx; });

// 4. Get corresponding monikers for Y-axis labels (fallback to address if moniker missing)
const monikers = addresses.map(addr => monikerMap[addr] || addr);

// 5. Control vertical spacing and jitter
const verticalSpacing = 50;
const jitterAmount = 0.3;

// 6. Prepare points & traces
const heightField = signingSeries.fields.find(f => f.name === 'height');
const signedField = signingSeries.fields.find(f => f.name === 'signed');
for (let i = 0; i < signingSeries.length; i++) {
  const xValue = heightField.values.get(i);
  const address = addressFieldSigning.values.get(i);
  const yValue = addressIndex[address] * verticalSpacing;
  const signed = signedField.values.get(i);

  traces.push({
    x: [xValue],
    y: [yValue],
    mode: 'markers',
    type: 'scatter',
    marker: {
      color: colors[signed],
      size: 20,
      symbol: 'square',
      line: { width: 2, color: 'black' }
    },
    showlegend: false
  });
}

// 7. Return plot config with monikers on Y-axis
return {
  data: traces,
  layout: {
    font: {
      family: 'Inter, Helvetica, Arial, sans-serif'
    },
    margin: { l: 0, r: 0, b: 0, t: 30 },
    xaxis: {
      title: 'Block Height',
      type: 'linear',
      autorange: true,
      automargin: true,
      tickformat: ',d'
    },
    yaxis: {
      title: 'Validator',
      type: 'linear',
      automargin: true,
      tickvals: addresses.map((_, idx) => idx * verticalSpacing),
      ticktext: monikers
    },
    showlegend: false
  }
};
