targets.expr:
avg by(name, address) (
  increase(
    tendermint_validator_missed_blocks_total{chain_id="$chain_id", address="$address", k8s_pod_start_time=""}[$interval]
  )
  +
  on(address) group_left(name)
  tendermint_validators{chain_id="$chain_id", name="$name", k8s_pod_start_time=""}
)

avg by(name, address) (
  increase(
    tendermint_validator_proposed_blocks_total{chain_id="$chain_id", address="$address", k8s_pod_start_time=""}[$__rate_interval]
  )
  +
  on(address) group_left(name)
  tendermint_validators{chain_id="$chain_id", name="$name", k8s_pod_start_time=""}
)
